def average_adjacent(data):
    if len(data) == 0:
        return []
    if len(data) == 1:
        return [data[0]["x"]]
    data = sorted(data, key=lambda v: v["time"])
    a, b = None, data[0]["x"]
    result = []
    for value in data[1:]:
        a = b; b = value["x"]
        result.append((a+b)/2)
    return result
