import copy
def get_mean(ls):
    sum = 0
    for e in ls:
        sum += e['value']
    return sum/len(ls)

def changing_list(list):
    start_mean = get_mean(list)
    for i,elem in enumerate(list):
        new_ls = copy.deepcopy(list)
        del new_ls[i]
        mean = get_mean(new_ls)
        if abs(mean - start_mean) < 0.1:
            return i
