def get_mean(ls):
  sum = 0
  for e in ls:
    sum += e['value']
  return sum/len(ls)

def changing_mean(values):
  start_mean = get_mean(values)
  for i,elem in enumerate(values):
    new_ls = values[:i] + values[i+1:]
    mean = get_mean(new_ls)
    if abs(mean - start_mean) < 0.1:
      return i
  return None
