def purchased_all_food(food, orders):
    n_food = len(food)
    unique_orders = defaultdict(set)
    for order in orders:
      unique_orders[order["buyer"]].add(order["food"])

    buyers = []
    for buyer, orders in unique_orders.items():
      if len(orders) == n_food:
        buyers.append(buyer)
    return buyers
