def reachable(graph, query):
    def step(visited):
        return set([
            edge["target"]
            for vertex in visited
            for edge in graph
            if vertex == edge["source"]
        ]).union(visited)

    def fix(f, x):
        next = f(x)
        return x if next == x else fix(f, next)

    source = query[0]["source"]
    return list(fix(step, set([source])))
