get_child: {[families; child]
  ?[families; (); 0b; `family`dob`height ! 
    (`family; `$("dob_child",child); `$("height_child",child))]};
row_per_child: {[families]
  ({[child] update child: (count families)#enlist ("child",child) 
    from get_child[families; child]} 
  each ("1"; "2"; "3")) ,/
  }
